import TodoItem from "../subComs/TodoItem";
import React, { Component } from "react";
import TaskView from "./TaskView";
import SubTaskView from "./SubTaskView";
import AddBar from "../subComs/AddBar";

export default class ListView extends Component {
  render() {
    // return this.props.todos.map(todo =>  <TodoItem
    //   key = {todo._id}
    //   todo={todo}
    //   editTodo = {this.props.editTodo}
    //   deleteTodo = {this.props.deleteTodo}
    //   /> )
    return this.props.todos.map(todo => (
      <div>
        <div className="d-flex">
          <span style={{ flexGrow: 3 }}>
            <TaskView
              key={todo._id}
              todo={todo}
              editTodo={this.props.editTodo}
              deleteTodo={this.props.deleteTodo}
            />
          </span>
          <button
            className="btn"
            type="button"
            data-toggle="collapse"
            data-target={"#subTaskCollapse "}
            aria-expanded="false"
            aria-controls={"subTaskCollapse " + todo._id}
          >
            V
          </button>
        </div>
        <div className="collapse" id={"subTaskCollapse " + todo._id}>
          {todo.subTasks.map(subTodo => (
            <div>
              <AddBar addTodo = {this.props.addTodo}/>
              <SubTaskView
                key={subTodo._id}
                todo={subTodo}
                editTodo={this.props.editTodo}
                deleteTodo={this.props.deleteTodo}
              />
            </div>
          ))}
        </div>
      </div>
    ));
  }
}
