import React, { Component } from 'react'
import Axios from 'axios'
import TodoView from '../Components/views/TodoView'
// let todoURL = "http://localhost:5000/todos"
let todoURL = '/todos'
// let userURL = "http://localhost:5000/users"
export default class HomePage extends Component {
  constructor(props) {
    super(props)

    this.state = {
       todos: [],
       headers:{
         authToken: ''}
    }
  }
  fetchData = () =>{
    Axios
      .get(todoURL,axiosConfig)
      .then(res => this.setState({todos: res.data.data}))
  }
  componentDidMount() {
    this.setState({authToken: this.props.authToken})
    this.fetchData()
  }
  addTodo = (todo) =>{
    Axios
      .post(todoURL,{title: todo},axiosConfig)
      .then(()=> this.fetchData())
  }
  editTodo = (_id,title,completed) =>{
    Axios
      .patch(`${todoURL}/${_id}`, {title,completed},axiosConfig)
      .then(()=> this.fetchData())
  }
  deleteTodo = (_id) => {
    Axios
      .delete(`${todoURL}/${_id}`,axiosConfig)
      .then(()=> this.fetchData())
  }

  render() {
    const axiosConfig = {
      headers: {
        authToken: this.state.authToken
      }
    }
    return (
      <div>
        <TodoView 
            todos = {this.state.todos}
            addTodo = {this.addTodo}
            editTodo = {this.editTodo}
            deleteTodo = {this.deleteTodo}
        />
      </div>
    )
  }
}
