import React, { Component } from 'react'
import Axios from 'axios'
import TodoView from '../Components/views/TodoView'
// let todoURL = "http://localhost:5000/todos"
let todoURL = '/todos'
// let userURL = "http://localhost:5000/users"
export default class HomePage extends Component {
  constructor(props) {
    super(props)

    this.state = {
       todos: [],
      //  axiosConfig:{
      //    headers:{
      //     authToken: ''
      //   }}
    }
  }
  fetchData = () =>{
    Axios
      .get(todoURL,{headers: this.props.authToken})
      .then(res => this.setState({todos: res.data.data}))
  }
  componentDidMount() {
    this.fetchData()
  }
  // componentDidUpdate(prevProps){
  //   if (this.props.authToken !== prevProps.authToken) this.setState({axiosConfig:{headers:{authToken: this.props.authToken} }})

  // }
  addTodo = (todo) =>{
    Axios
      .post(todoURL,{title: todo},{headers: this.props.authToken})
      .then(()=> this.fetchData())
  }
  editTodo = (_id,title,completed) =>{
    Axios
      .patch(`${todoURL}/${_id}`, {title,completed},{headers: this.props.authToken})
      .then(()=> this.fetchData())
  }
  deleteTodo = (_id) => {
    Axios
      .delete(`${todoURL}/${_id}`,this.state.axiosConfig)
      .then(()=> this.fetchData())
  }

  render() {

    return (
      <div>
        <TodoView 
            todos = {this.state.todos}
            addTodo = {this.addTodo}
            editTodo = {this.editTodo}
            deleteTodo = {this.deleteTodo}
        />
      </div>
    )
  }
}
