import React, { Component } from "react";
import Axios from "axios";
import TodoView from "../Components/views/TodoView";
// let todoURL = "http://localhost:5000/todos"
let todoURL = "/todos";
// let userURL = "http://localhost:5000/users"
export default class HomePage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      todos: []
    };
  }
  fetchData = () => {
    Axios.get(todoURL, {
      headers: { 'authToken': this.props.authToken }
    }).then(res => this.setState({ todos: res.data.data }));
  };
  componentDidMount() {
    this.fetchData();
  }
  addTodo = todo => {
    Axios.post(
      todoURL,
      { title: todo },{
      headers: { 'authToken': this.props.authToken }
      }).then(() => this.fetchData());
  };
  editTodo = (_id, title, completed) => {
    Axios.patch(
      `${todoURL}/${_id}`,
      { title, completed },{
        headers: { 'authToken': this.props.authToken }
        }).then(() => this.fetchData());
  };
  deleteTodo = _id => {
    Axios.delete(`${todoURL}/${_id}`,{
      headers: { 'authToken': this.props.authToken }
      }).then(() => this.fetchData());
  };

  render() {
    return (
      <div>
        <TodoView
          todos={this.state.todos}
          addTodo={this.addTodo}
          editTodo={this.editTodo}
          deleteTodo={this.deleteTodo}
        />
      </div>
    );
  }
}
