import React, { Component } from "react";
import { Link } from "@reach/router";
import { FiInfo } from "react-icons/fi";
let strike = "";
export default class TodoItem extends Component {
  constructor(props) {
    super(props);

    this.state = {
      isInfo: false,
    };
  }

  render() {
    let todo = this.props.todo;
    strike = todo.completed === true ? "line-through" : "";
    return (
      <div
        className="form-check border-bottom py-3"
        onMouseEnter={() => this.setState({ isInfo: true })}
        onMouseLeave={() => this.setState({ isInfo: false })}
      >
        <input
          className="form-check-input"
          type="checkbox"
          defaultChecked={todo.completed}
          value={todo.title}
          onChange = {()=>this.props.editTodo(todo,todo.title,!todo.completed)}
        />
        <label className="form-check-label" style={{ textDecoration: strike }}>
          {todo.title}
        </label>
        {this.state.isInfo && (
          <span className="float-right">
            <Link to={`/${todo._id}`}>
              <FiInfo />
            </Link>
          </span>
        )}
      </div>
    );
  }
}
