import React, { Component } from "react";
import { Link } from "@reach/router";
import Background from "./todoBG.jpg";
// import '../../src/FrontPage.css'
export default class FrontPage extends Component {
  render() {
    return (
      <div className="text-dark">
        {/* <img class="" src={Background} style={imageStyle} alt = 'background'/> */}
        <div className="" style = {textStyle}>
          <h3>Here's your favorite Todo Website amirite?</h3>
          <p>
            my first website ever *padding myself*
          </p>
          <Link className="btn btn-primary mx-1" to="/register">
            Register
          </Link>
          <Link className="btn btn-outline-primary mx-1" to="/login">
            Log in
          </Link>
        </div>
      </div>
    );
  }
}
const textStyle = {
    position: 'absolute',
    left: '0',
    top: '30%',
    width: '100%',
    textAlign: 'center'
}
const imageStyle = {
    position: 'relative',
    display: 'table',
    top: '0',
    left: '0',
    width: '100%',
    height: 'auto',
    zIndex: '-1'
}
