import React, { Component } from 'react'
// import '../Style/App.css';
import Axios from 'axios'
import { Router } from '@reach/router'
import HomePage from './Pages/HomePage';
import NotFound from './Pages/NotFoundPage';
import LoginPage from './Pages/LoginPage';
import RegisterPage from './Pages/RegisterPage';
let todoURL = "http://localhost:5000/todos"
let userURL = "http://localhost:5000/users"
export default class App extends Component {
  constructor(props) {
    super(props)

    this.state = {
       todos: [],
       user: ''
    }
  }
  fetchData = () =>{
    Axios
      .get(todoURL)
      .then(res => this.setState({todos: res.data.data}))
  }
  componentDidMount() {
    this.fetchData()
  }
  addTodo = (todo) =>{
    Axios
      .post(todoURL,{title: todo})
      .then(()=> this.fetchData())
  }
  editTodo = (_id,title,completed) =>{
    Axios
      .patch(`${todoURL}/${_id}`, {title,completed})
      .then(()=> this.fetchData())
  }
  deleteTodo = (_id) => {
    Axios
      .delete(`${todoURL}/${_id}`)
      .then(()=> this.fetchData())
  }

  registerUser = (user) => {
    Axios
      .post(userURL + "/signup",user)
      .then(res => console.log(res) )
  }


  render() {
    return (
      <div>
        {/* <Nav /> */}
        <Router>
          <HomePage path = '/' 
            todos = {this.state.todos}
            addTodo = {this.addTodo}
            editTodo = {this.editTodo}
            deleteTodo = {this.deleteTodo}
            />
          <LoginPage path = '/login'/>
          <RegisterPage path = '/register'
            registerUser = {this.registerUser}
          />
        </Router>
      </div>
    );
  }
}
